// SPDX-License-Identifier: MIT
pragma solidity >=0.4.21 <0.7.0;

contract TodoList {
    // constructor() public {
    //     createTodo("complete eth-react-Todo application till 1 PM, today");
    // }

    struct Todo {
        uint256 id;
        string content;
        bool isComplete;
    }
    uint256 public todoCount = 0;

    mapping(uint256 => Todo) public todos;

    event TodoCreated(uint256 id, string content, bool isComplete);

    event markTodoState(uint256 id, bool isComplete);

    function createTodo(string memory _content) public {
        todoCount ++;
        todos[todoCount] = Todo(todoCount, _content, false);
        emit TodoCreated(todoCount, _content, false);
    }

    function toggleTodoState(uint256 _id) public {
        Todo memory _todo = todos[_id];
        _todo.isComplete = !_todo.isComplete;
        todos[_id] = _todo;
        emit markTodoState(_id, _todo.isComplete);
    }
}
